// src/components/SocialMediaScreen.js

import React, { useState, useEffect } from "react";
import axios from "axios";

const BACKEND_URL = "http://localhost:5000";

// Dynamically generates a prompt for any mood
function generatePromptForMood(mood) {
  if (!mood || typeof mood !== "string" || mood.trim() === "") {
    mood = "neutral";
  }
  return `Create a high-resolution, realistic square social-media–style image that mentally balances the mood “${mood}.” Follow these conditional guidelines based on the valence of the emotion: 1. **If “${mood}” is a negative emotion** (e.g., scared, anxious, sad, angry, overwhelmed): - **Fear / Anxiety**: Depict a scene that instills confidence and safety. For example, a lone figure standing tall on a sunlit hilltop, shoulders relaxed, looking out over a calm valley as golden-hour light surrounds them—symbolizing strength and reassurance. - **Anger / Frustration**: Show a peaceful, restorative environment that soothes tension. For example, close-up of a gently rippling lake at dawn with soft pastel reflections, or a slow-motion shot of green leaves rustling in a light breeze—elements that convey stillness and let the viewer release steam. - **Sadness / Loneliness**: Choose a comforting, warm-toned interior or natural vignette. For example, a cozy reading nook by a window with a plush armchair, a steaming mug of tea on a wooden side table, and soft, muted lighting that wraps around the scene—evoking a sense of gentle consolation and being cared for. - **Overwhelm / Stress**: Illustrate a simple mindful ritual in progress. For instance, a person seated cross-legged on a wooden floor mat, eyes closed, palms resting on knees with a single burning candle in front of them, soft focus background—signaling a moment of pause and centered breathing. 2. **If “${mood}” is a positive emotion** (e.g., happy, excited, hopeful, grateful, loving): - **Happiness / Joy**: Show a scene that amplifies or resonates with joy. For example, a group of friends mid-laughter at an outdoor picnic under string lights, vibrant flowers in full bloom in the background, and natural sunlight highlighting everyone’s smiles—conveying warmth and exuberance. - **Excitement / Hope**: Depict a moment of gentle celebration or anticipation. For instance, a person reaching toward a sunrise horizon with outstretched arms, soft lens flare touching their face, conveying openness to new possibilities. - **Gratitude / Love**: Choose an intimate, heart-warming tableau. For example, two hands holding delicate wildflowers against a blurred meadow at golden hour, warm hues bathing the petals—symbolizing appreciation and connection. 3. **If “${mood}” is neutral or ambiguous** (e.g., calm, indifferent, contemplative, neither particularly up nor down): - **Neutral / Contemplative**: Present a random, visually pleasing everyday moment without strong emotional cues. For example, a still-life shot of a simple breakfast: fresh fruit on a ceramic plate beside a steaming cup of coffee on a rustic wood table, morning sunlight slanting across the scene—an unassuming snapshot that invites the viewer to project their own calm. **Overall visual requirements (apply in all cases)**: - **Realism & Mood Harmony**: The image must look like a genuine social-media post—clear focus, realistic lighting, and believable textures (skin, fabric, wood, water, foliage, etc.). Choose a color palette that complements the intended emotional balance: warm neutrals for comfort, soft pastels for calm, or vibrant but not jarring hues for uplifting joy. - **Composition & Focus**: Use a centered or gently off-center subject that directs the viewer’s eye toward the balancing element (e.g., the confident figure, the tranquil lake, the cozy nook, or the joyful gathering). Blur or de-emphasize background clutter so the focal point feels inviting rather than overwhelming. - **Symbolic Details**: Include one or two subtle, symbolic objects that reinforce mental balance—for example, a single stack of balanced stones near a shoreline for stability, a candle flame for focus, a blooming flower for renewal, or a shared smile for connection. These details should be integrated naturally, not as floating icons. - **Atmosphere & Lighting**: Rely on soft, natural lighting—early-morning or golden-hour light works best. Avoid harsh shadows or overly saturated colors. If indoors, let warm window light spill in; if outdoors, position subjects in open shade or gentle sunbeams. - **Text & Overlays**: Do not insert large text captions or UI elements. If any words appear, they should be minimal (e.g., a small, unobtrusive handwritten word like “Breathe” integrated into a corner of the frame, drawn as if on a wall or piece of paper). The focus must remain on the photograph-like scene itself.`;
}

function SocialMediaScreen({ mood }) {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // For debugging: see what mood is coming in
  useEffect(() => {
    console.log("Received mood from backend:", mood);
  }, [mood]);

  useEffect(() => {
    async function fetchImages() {
      setLoading(true);
      setError(null);
      setImages([]);
      const prompt = generatePromptForMood(mood);
      try {
        const response = await axios.post(`${BACKEND_URL}/api/generate-image`, {
          prompt,
        });
        setImages(response.data.images || []);
      } catch (e) {
        setImages([]);
        setError(
          e.response && e.response.data && e.response.data.error
            ? e.response.data.error
            : "Image generation failed."
        );
      }
      setLoading(false);
    }
    fetchImages();
  }, [mood]);

  return (
    <div style={{ maxWidth: 700, margin: "50px auto", textAlign: "center" }}>
      <div
        style={{
          marginBottom: 32,
          padding: "18px 30px",
          background: "#f3f7ff",
          borderRadius: 12,
          border: "1px solid #dbeafe",
          fontSize: 20,
          color: "#183153",
          textAlign: "center",
          boxShadow: "0 2px 8px #0001",
        }}
      >
        <span style={{ fontWeight: 500, fontSize: 22, color: "#375bb6" }}>
          Mood detected:&nbsp;
        </span>
        <span style={{ fontWeight: 700, fontSize: 22, textTransform: "capitalize" }}>
          {mood || "neutral"}
        </span>
        <div style={{ marginTop: 12, fontSize: 17 }}>
          The feed below is generated by AI to visually reflect your mood.
        </div>
      </div>
      <h2>Social Mood Feed</h2>
      {error && (
        <div style={{ color: "red", margin: 16 }}>{error}</div>
      )}
      {loading ? (
        <p>Generating images...</p>
      ) : (
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            flexWrap: "wrap",
            gap: 20,
          }}
        >
          {images.length > 0 ? (
            images.map((img, idx) => (
              <div
                key={idx}
                style={{
                  border: "1px solid #ddd",
                  borderRadius: 10,
                  padding: 10,
                }}
              >
                <img
                  src={img}
                  alt={`mood img ${idx}`}
                  style={{
                    width: 200,
                    height: 200,
                    objectFit: "cover",
                    borderRadius: 10,
                  }}
                />
              </div>
            ))
          ) : (
            <div style={{ color: "#666" }}>No images generated.</div>
          )}
        </div>
      )}
    </div>
  );
}

export default SocialMediaScreen;
