// src/components/SocialMediaScreen.js

import React, { useState, useEffect } from "react";
import axios from "axios";

const BACKEND_URL = "http://localhost:5000";

// Create a prompt that uses the detected mood dynamically
function generatePromptForMood(mood) {
  // This template uses the detected mood to influence the AI image
  return `An artistic image that visually represents the mood "${mood}".`;
}

// A simple mood explanation template
function getMoodDescription(mood) {
  return `Your detected mood is "${mood}". The social mood feed below is generated by AI to visually reflect this mood.`;
}

function SocialMediaScreen({ mood }) {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Always fallback to "neutral" if mood is missing or empty
  const normalizedMood = mood && typeof mood === "string" && mood.length > 0
    ? mood
    : "neutral";
  const prompt = generatePromptForMood(normalizedMood);

  useEffect(() => {
    async function fetchImages() {
      setLoading(true);
      setError(null);
      setImages([]);
      try {
        const response = await axios.post(`${BACKEND_URL}/api/generate-image`, { prompt });
        setImages(response.data.images || []);
      } catch (e) {
        setImages([]);
        setError(
          e.response?.data?.error || "Image generation failed."
        );
      }
      setLoading(false);
    }
    fetchImages();
  }, [prompt]);

  return (
    <div style={{ maxWidth: 700, margin: "50px auto", textAlign: "center" }}>
      <div
        style={{
          marginBottom: 32,
          padding: "18px 30px",
          background: "#f3f7ff",
          borderRadius: 12,
          border: "1px solid #dbeafe",
          fontSize: 20,
          color: "#183153",
          textAlign: "center",
          boxShadow: "0 2px 8px #0001",
        }}
      >
        <span style={{ fontWeight: 500, fontSize: 22, color: "#375bb6" }}>
          Mood detected:&nbsp;
        </span>
        <span style={{ fontWeight: 700, fontSize: 22, textTransform: "capitalize" }}>
          {normalizedMood}
        </span>
        <div style={{ marginTop: 12, fontSize: 17 }}>
          {getMoodDescription(normalizedMood)}
        </div>
      </div>
      <h2>Social Mood Feed</h2>
      {error && (
        <div style={{ color: "red", margin: 16 }}>{error}</div>
      )}
      {loading ? (
        <p>Generating images...</p>
      ) : (
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            flexWrap: "wrap",
            gap: 20,
          }}
        >
          {images.length > 0 ? (
            images.map((img, idx) => (
              <div
                key={idx}
                style={{
                  border: "1px solid #ddd",
                  borderRadius: 10,
                  padding: 10,
                }}
              >
                <img
                  src={img}
                  alt={`mood img ${idx}`}
                  style={{
                    width: 200,
                    height: 200,
                    objectFit: "cover",
                    borderRadius: 10,
                  }}
                />
              </div>
            ))
          ) : (
            <div style={{ color: "#666" }}>No images generated.</div>
          )}
        </div>
      )}
    </div>
  );
}

export default SocialMediaScreen;
