// src/components/SocialMediaScreen.js

import React, { useState, useEffect } from "react";
import axios from "axios";

const BACKEND_URL = "http://localhost:5000"; // adjust if your server runs elsewhere

/**
 * Calls your backend‚Äôs `/api/mood-description` endpoint.
 * Returns a 1‚Äì2 sentence description from OpenAI, or a simple fallback if it fails.
 */
async function getMoodDescription(mood) {
  if (!mood || typeof mood !== "string") {
    return "";
  }

  try {
    const res = await axios.post(
      `${BACKEND_URL}/api/mood-description`,
      { mood: mood.trim() }
    );
    return res.data.description; // e.g. "When someone feels happy, it often means‚Ä¶"
  } catch (err) {
    console.error("Failed to fetch mood description:", err);
    // Fallback: a simple local message
    return `You seem to be feeling ${mood}.`;
  }
}

/**
 * -------------------------------------------------------------
 *  DEFAULT EXPORT: SocialMediaScreen
 *
 *  - Shows a header with ‚ÄúMood detected: X‚Äù and a 1‚Äì2 sentence
 *    description (fetched from /api/mood-description).
 *  - Renders <SocialMediaFeed mood={mood} /> underneath.
 * -------------------------------------------------------------
 */
export default function SocialMediaScreen({ userMessages }) {
  
  const [mood, setMood] = useState("");            
  const [description, setDescription] = useState(""); 
  const [loadingDesc, setLoadingDesc] = useState(false);

  // 1) Whenever `userMessages` changes, call `/api/mood`.
  useEffect(() => {
    async function fetchMood() {
      if (!Array.isArray(userMessages) || userMessages.length === 0) {
        // No user messages yet‚Äîclear mood
        setMood("");
        return;
      }

      try {
        // Call your backend‚Äôs `/api/mood` exactly as defined in server.js:
        //    POST /api/mood 
        //    Body: { userMessages: [ "I feel sad", "I‚Äôm lonely", ‚Ä¶ ] }
        const res = await axios.post(`${BACKEND_URL}/api/mood`, { userMessages });
        const newMood = res.data.mood; // e.g. "happy", "sad", etc.
        setMood(newMood);
      } catch (err) {
        console.error("Error fetching /api/mood:", err.response?.data || err.message);
        setMood("neutral"); // fallback if the API fails
      }
    }

    fetchMood();
  }, [userMessages]);


  // 2) Whenever `mood` changes, fetch a 1‚Äì2 sentence description
  //    from `/api/mood-description`. Until it arrives, show a placeholder.
  useEffect(() => {
    let isMounted = true;
    async function fetchDescription() {
      if (!mood) {
        if (isMounted) setDescription("");
        return;
      }
      // Show a loading placeholder while waiting
      if (isMounted) {
        setLoadingDesc(true);
        setDescription("Loading description‚Ä¶");
      }

      const desc = await getMoodDescription(mood);
      if (isMounted) {
        setDescription(desc);
        setLoadingDesc(false);
      }
    }
    fetchDescription();

    return () => {
      isMounted = false;
    };
  }, [mood]);


  return (
    <div style={{ maxWidth: 600, margin: "0 auto", padding: "44px 0" }}>
      {/* ‚îÄ‚îÄ‚îÄ Header / Text Banner ‚îÄ‚îÄ‚îÄ */}
      <div
        style={{
          marginBottom: 32,
          padding: "18px 30px",
          background: "#f3f7ff",
          borderRadius: 12,
          border: "1px solid #dbeafe",
          fontSize: 20,
          color: "#183153",
          textAlign: "center",
          boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        }}
      >
        <span style={{ fontWeight: 500, fontSize: 22, color: "#375bb6" }}>
          Mood detected:&nbsp;
        </span>
        <span
          style={{
            fontWeight: 700,
            fontSize: 22,
            textTransform: "capitalize",
          }}
        >
          {mood || "‚Äî"}
        </span>
        <div style={{ marginTop: 12, fontSize: 17, minHeight: 24 }}>
          {loadingDesc ? "Loading description‚Ä¶" : description}
        </div>
      </div>

      {/* ‚îÄ‚îÄ‚îÄ Below the header, render the feed ‚îÄ‚îÄ‚îÄ */}
      <SocialMediaFeed mood={mood} />
    </div>
  );
}


/**
 * -------------------------------------------------------------
 *  NAMED EXPORT: SocialMediaFeed
 *
 *  - Shows the ‚ÄúGenerate Mood Feed‚Äù button.
 *  - When clicked, uses the passed-in `mood` to:
 *      1) Call `/api/generate-prompt` ‚Üí returns `{ dallePrompt }`
 *      2) Call `/api/generate-image`   ‚Üí returns `{ images: ["<url>"] }`
 *  - Displays the post (avatar + image + caption).
 * -------------------------------------------------------------
 */
export function SocialMediaFeed({ mood }) {
  const [post, setPost] = useState(null);  
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // 1) Generate a DALL¬∑E prompt from the mood
  async function getPrompt(mood) {
    const res = await axios.post(`${BACKEND_URL}/api/generate-prompt`, { mood });
    return res.data.dallePrompt;
  }

  // 2) Generate an image from that prompt
  async function getImage(prompt) {
    const res = await axios.post(`${BACKEND_URL}/api/generate-image`, { prompt });
    // The backend returns { images: ["<url>"] }
    return res.data.images[0];
  }

  // Called when you click ‚ÄúGenerate Mood Feed‚Äù
  const handleGenerateFeed = async () => {
    setLoading(true);
    setError(null);
    setPost(null);

    try {
      // If `mood` is empty, default to "neutral"
      const extractedMood = mood || "neutral";

      // 1) Get a DALL¬∑E prompt for this mood:
      const promptText = await getPrompt(extractedMood);

      // 2) Generate an AI image from that prompt:
      const imageUrl = await getImage(promptText);

      // 3) Build the post object
      setPost({
        mood: extractedMood,
        prompt: promptText,
        imageUrl,
      });
    } catch (e) {
      console.error("Error generating feed:", e.response?.data || e.message);
      setError("Failed to generate feed. Please try again.");
    }

    setLoading(false);
  };

  return (
    <div
      style={{
        maxWidth: 600,
        margin: "40px auto",
        padding: 24,
        background: "#fafafa",
        borderRadius: 16,
        boxShadow: "0 3px 12px rgba(0,0,0,0.08)",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: 20 }}>
        Social Mood Feed
      </h2>

      <button
        onClick={handleGenerateFeed}
        disabled={loading}
        style={{
          background: "#333",
          color: "#fff",
          fontSize: 18,
          border: "none",
          borderRadius: 8,
          padding: "12px 24px",
          margin: "0 auto 30px auto",
          display: "block",
          cursor: loading ? "wait" : "pointer",
        }}
      >
        {loading ? "Generating..." : "Generate Mood Feed"}
      </button>

      {error && <div style={{ color: "red", margin: 16 }}>{error}</div>}

      {post && (
        <div
          style={{
            background: "#fff",
            borderRadius: 12,
            boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
            overflow: "hidden",
          }}
        >
          {/* ‚îÄ‚îÄ‚îÄ Header (avatar + username) ‚îÄ‚îÄ‚îÄ */}
          <div
            style={{
              display: "flex",
              alignItems: "center",
              padding: "12px 16px",
              borderBottom: "1px solid #eee",
            }}
          >
            <div
              style={{
                width: 36,
                height: 36,
                borderRadius: "50%",
                backgroundImage: `url("https://i.pravatar.cc/150?img=12")`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                marginRight: 12,
              }}
            />
            <span style={{ fontWeight: 600, fontSize: 16 }}>AI_Generator</span>
          </div>

          {/* ‚îÄ‚îÄ‚îÄ AI-generated image ‚îÄ‚îÄ‚îÄ */}
          <img
            src={post.imageUrl}
            alt="AI generated"
            style={{
              width: "100%",
              display: "block",
              objectFit: "cover",
              maxHeight: 500,
            }}
          />

          {/* ‚îÄ‚îÄ‚îÄ Icon bar (like / comment / share) ‚îÄ‚îÄ‚îÄ */}
          <div
            style={{
              display: "flex",
              alignItems: "center",
              padding: "8px 16px",
              gap: 16,
            }}
          >
            <span role="img" aria-label="like" style={{ fontSize: 24, cursor: "pointer" }}>
              ‚ù§Ô∏è
            </span>
            <span role="img" aria-label="comment" style={{ fontSize: 24, cursor: "pointer" }}>
              üí¨
            </span>
            <span role="img" aria-label="share" style={{ fontSize: 24, cursor: "pointer" }}>
              üì§
            </span>
          </div>

          {/* ‚îÄ‚îÄ‚îÄ Caption (Prompt + hashtags) ‚îÄ‚îÄ‚îÄ */}
          <div style={{ padding: "0 16px 16px 16px" }}>
            <span style={{ fontWeight: 600, marginRight: 8 }}>AI_Generator</span>
            <span style={{ color: "#333" }}>{post.prompt}</span>
            <div style={{ marginTop: 8, color: "#999", fontSize: 14 }}>
              #{post.mood}vibes
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
